import React, {useState, useEffect, useCallback} from "react";

import MoviesList from "./MoviesList";
import Card from "../UI/Card/Card";

function MovieCon() {
    const [movies, setMovies] = useState([]);

    function fetchMoviesHandler() {
        fetch('http://www.omdbapi.com/?i=tt3896198&apikey=b98571b0').then(response => {
            response.json();
        }).then(data =>{
            const transformedMovies = data.results.map(movieData => {
                return {
                    type: movieData.type,
                    t: movieData.title,
                    y: movieData.yearOfRelease,
                    plot: movieData.plot
                };
            });
            setMovies(data.results);
        });
    }
    return (
        <React.Fragment>
            <Card>
                <form>
                <label htmlFor="movieName">Enter Movie Name</label>
                <input
                       type="movieName"
                       id="movieName"
                       value={transformedMovies.title}
                />
            <section>
            <button>Fetch Movies</button>
            </section>
               </form>
            </Card>
        </React.Fragment>
    )
};
export default MovieCon;
