import React, {useState, useEffect} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

import MoviesList from "./MoviesList";
import Button from "../UI/Button/Button";
import SearchBox from "../UI/SearchBox/SearchBox";

function MovieCon() {
  const [movies, setMovies] = useState([]);
  const [searchValue,setSearchValue] = useState('');

  const fetchMoviesHandler = async (searchValue) => {
    const url = `http://www.omdbapi.com/?apikey=b98571b0&s=${searchValue}`;

    const response = await fetch(url);
    const responseJson = await response.json();

    const transformedMovie = responseJson.movies.results.map(movies => {
        return {
            id: movies.id,
            title: movies.Title,
            releaseDate: movies.Released,
            type: movies.Type,
            poster: movies.Poster
        };
    });
    setMovies(transformedMovie);

    if(responseJson.Search){
        setMovies(responseJson.Search);
    }    
  };

  useEffect(() => {
    fetchMoviesHandler(searchValue);
  }, [searchValue]);

  return (
      
      <div className="container-fluid movie-app">
        <div className="row d-flex align-items-center mt-4 mb-4">
            <SearchBox searchValue={searchValue} setSearchValue={setSearchValue} />
        </div>
        <Button onClick={fetchMoviesHandler}>Search</Button>
        <div className="row">
            <MoviesList 
            key={movies.i}
            title={movies.t}
            releaseDate={movies.y}
            type={movies.type} />
        </div>
      </div>
  );
}

export default MovieCon;
